package java_project;

public class assgn_5_seconds_to_min_hour {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// Convert seconds to hour, minutes and seconds
		
		int second=1;
		int minutes=60*second;
		int hour=60*minutes;
		{
			System.out.println("1 second is "+second+ " second");
			System.out.println("1 minute is " +minutes+ " second");
			System.out.println("1 hour is " +hour+ " second" );
		}
	}

}
