package java_project;

public class if_and_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=2;
		int f=4;
				int j=10;
		
		if(x>f)
		{
			System.out.println("x is more than f");
		
		}
		else
		{
			System.out.println("f is more than x");
		}
		if (j>x)
		{
			System.out.println("j is more than x");
		}
		else
		{
			System.out.println("x is more than j");
		}
	}

}
